// Premium Chauffeur bundled JavaScript file
// This would typically be generated by a build tool like Webpack
// For demo purposes, we're simulating the bundled output

// Initialize the app
document.addEventListener('DOMContentLoaded', () => {
    console.log('DOM Content Loaded - Initializing app');
    const appRoot = document.getElementById('app');

    if (appRoot) {
        // Clear any existing content first
        appRoot.innerHTML = '';
        console.log('Cleared app root content');

        // Remove any existing headers from the entire document
        const existingHeaders = document.querySelectorAll('header.site-header, header.futuristic-header');
        console.log(`Found ${existingHeaders.length} existing headers to remove`);
        existingHeaders.forEach(header => {
            header.remove();
            console.log('Removed existing header');
        });

        // Create main structure
        const premiumChauffeur = document.createElement('div');
        premiumChauffeur.className = 'premium-chauffeur';
        console.log('Created premium-chauffeur container');

        // Create destination detail modal first to ensure it's in the DOM
        createDestinationModal();

        // Check if we're on a service page
        const isServicePage = window.location.pathname.includes('/services/');
        console.log(`Is service page: ${isServicePage}`);

        // Add header only if not on a service page
        if (!isServicePage) {
            const header = createHeader();
            premiumChauffeur.appendChild(header);
            console.log('Added header to premium-chauffeur container');
        }

        // Create main element
        const main = document.createElement('main');
        console.log('Created main element');

        // Add sections to main
        main.appendChild(createHeroSection());
        main.appendChild(createServicesSection());
        main.appendChild(createAboutSection());
        main.appendChild(createDestinationsSection());
        main.appendChild(createTourConfiguratorSection());
        main.appendChild(createContactSection());
        console.log('Added all sections to main');

        // Add main to the app
        premiumChauffeur.appendChild(main);

        // Add footer
        const footer = createFooter();
        premiumChauffeur.appendChild(footer);
        console.log('Added footer to premium-chauffeur container');

        // Add the entire structure to the app
        appRoot.appendChild(premiumChauffeur);
        console.log('Added premium-chauffeur container to app root');

        // Initialize the functionality
        initializeMainScript();
        console.log('Initialized main script');

        // Initialize slider after a short delay
        setTimeout(() => {
            setupSlider();
            console.log('Slider setup complete');

            // Setup mobile menu with a delay to ensure DOM is ready
            setTimeout(() => {
                setupMobileMenu();
                console.log('Mobile menu setup triggered from bundle.js');
            }, 300);
        }, 500);

        // Add scroll event listener for sticky header effect
        if (!isServicePage) {
            window.addEventListener('scroll', () => {
                const header = document.querySelector('.futuristic-header');
                if (header) {
                    if (window.scrollY > 50) {
                        header.classList.add('sticky');
                    } else {
                        header.classList.remove('sticky');
                    }
                }
            });
            console.log('Added scroll event listener for sticky header');
        }
    }
});

// Separate function to initialize main script
function initializeMainScript() {
    // Remove any existing main script
    const existingScript = document.querySelector('script[src="/js/main.js"]');
    if (existingScript) {
        existingScript.remove();
    }

    // Add the main script
    const mainScript = document.createElement('script');
    mainScript.src = 'js/main.js';
    document.body.appendChild(mainScript);
}

// Direct implementation of mobile menu setup in bundle.js
function setupMobileMenu() {
    console.log('Setting up mobile menu from bundle.js...');

    // Try to find the mobile menu button by ID first
    let mobileMenuBtn = document.getElementById('mobile-menu-toggle');

    // If not found by ID, try to find by class as fallback
    if (!mobileMenuBtn) {
        console.log('Mobile menu button not found by ID, trying class selector');
        mobileMenuBtn = document.querySelector('.mobile-menu-btn');
    }

    const mainNav = document.querySelector('.main-nav');

    // Check if we're on mobile view
    const isMobileView = () => window.innerWidth <= 992;

    if (!mobileMenuBtn || !mainNav) {
        console.error('Mobile menu elements not found:', {
            mobileMenuBtn: !!mobileMenuBtn,
            mainNav: !!mainNav
        });
        console.log('Will try again in 1000ms');
        setTimeout(setupMobileMenu, 1000);
        return;
    }

    console.log('Mobile menu elements found:', {
        mobileMenuBtn: mobileMenuBtn,
        mainNav: mainNav
    });

    // Initial setup for desktop view
    if (!isMobileView()) {
        mainNav.style.transform = 'none';
    }

    // Toggle mobile menu with direct click handler
    mobileMenuBtn.onclick = function(e) {
        e.preventDefault();
        console.log('Mobile menu button clicked');
        this.classList.toggle('active');
        mainNav.classList.toggle('active');

        // Log the current state
        console.log('Menu state after click:', {
            buttonActive: this.classList.contains('active'),
            navActive: mainNav.classList.contains('active')
        });
    };

    // Also add the ID to ensure future references work
    if (!mobileMenuBtn.id) {
        mobileMenuBtn.id = 'mobile-menu-toggle';
        console.log('Added ID to mobile menu button');
    }

    // Handle resize events
    window.addEventListener('resize', () => {
        if (!isMobileView()) {
            // Reset styles for desktop view
            mainNav.style.transform = 'none';

            // Remove active classes
            mobileMenuBtn.classList.remove('active');
            mainNav.classList.remove('active');
        } else {
            // Reset for mobile view
            if (!mainNav.classList.contains('active')) {
                mainNav.style.transform = '';
            }
        }
    });

    console.log('Mobile menu setup complete from bundle.js');
}

